
package com.javaExample.ex02;

/**
 *
 */
public class Fruit {
	private String name;
	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @return the color
	 */
	public String getColor() {
		return color;
	}

	private String color;
	
	Fruit(String name, String color) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.color = color;
	}
	

}
